mapper.select:
SELECT b.board_id AS boardId, b.category, b.title, b.view_count AS viewCount, b.created_at AS createdAt, u.user_id AS writerId, u.nickname AS writerNickname, (SELECT COUNT(*) FROM comment c WHERE c.board_id = b.board_id) AS commentCount FROM board b JOIN users u ON b.writer_id = u.user_id WHERE b.crew_id = #{crewId}
AND b.category = #{category}
AND b.title LIKE CONCAT('%', #{keyword}, '%')
ORDER BY b.created_at DESC LIMIT #{size} OFFSET #{offset}
